import React, { useState, useEffect } from "react";
import ContextCart from "./ContextCart";

const ContextCartProvice = (props) => {
  const [items, setItem] = useState([]);
  //viết hàm tìm trong giỏ hàng có sản phẩm chưa
  const findItem = (id) => {
    return items.findIndex((item) => item.id === id);
  };
  const addItem = (item) => {
    setItem((preItem) => {
      console.log(items);
      if (findItem(item.id) < 0) {
        items.push(item);
        return [...preItem];
      } else {
        items[findItem(item.id)].amount =
          items[findItem(item.id)].amount + item.amount;
        return [...preItem];
      }
    });
  };
  //tính tổng tiền
  let totalAmount = +0;
  for (let i = 0; i < items.length; i++) {
    let tong = items[i].amount * items[i].price;

    totalAmount = tong;
  }
  //remove
  const removeItem = (itemID) => {
    setItem((preItem) => {
      preItem[findItem(itemID)].amount -= 1;
      console.log(findItem(itemID));
      if (preItem[findItem(itemID)].amount === 0) {
        preItem.remove(preItem[findItem(itemID)]);
      }
      return [...preItem];
    });
  };
  return (
    <ContextCart.Provider
      value={{
        items: items,
        totalAmount: totalAmount,
        addItem: addItem,
        removeItem: removeItem,
      }}
    >
      {props.children}
    </ContextCart.Provider>
  );
};
export default ContextCartProvice;
