  const findItem = (id) => {
    return items.findIndex((item) => item.id === id);
  };
  const addItem = (item) => {
    // console.log(findItem(item.id));
    // setItem((preItem) => {
    //   if (findItem(item.id) < 0) {
    //     return [...preItem, item];
    //   } else {
    //     return [...preItem];
    //   }
    // });
    // console.log("load");
    // console.log(items);
  };
  return (
    <ContextCart.Provider
      value={{
        items: items,
        totalAmount: 0,
        addItem: addItem,
        removeItem: (id) => {},
      }}
    >
      {props.children}
    </ContextCart.Provider>
  );
};
export default ContextCartProvice;
