import React, { useState } from "react";
import ContextCart from "./ContextCart";

const ContextCartProvice = (props) => {
  const [items, setItem] = useState([]);
  const addItem = (item) => {
    setItem((preItem) => {
      return { ...preItem, item };
    });
  };

  return (
    <ContextCart.Provider
      value={{
        items: { items },
        totalAmount: 0,
        addItem: addItem,
        removeItem: (id) => {},
      }}
    >
      {props.children}
    </ContextCart.Provider>
  );
};
export default ContextCartProvice;
